// une fonction qui applique l'algorithme de Dijkstra pour trouver le plus court chemin entre
// un seul nœud source et tous les autres nœuds d'un graphe. Le graphe est représenté en utilisant
// une matrice d'adjacence.
void dijkstra(int graph[V][V], int src)
{
    int dist[V];
    bool processed[V];
    int i, u, v;

    // Initialiser toutes les distances comme infinies et traitées comme fausses
    for (i = 0; i < V; i++) {
        dist[i] = INT_MAX;
        processed[i] = false;
    }

    // La distance du sommet source à lui-même est toujours 0
    dist[src] = 0;

    // Trouver le plus court chemin pour tous les sommets
    for (i = 0; i < V - 1; i++) {
        // Choisir le sommet de distance minimale parmi l'ensemble de sommets non encore traités
        u = -1;
        for (i = 0; i < V; i++) {
            if (!processed[i] && (u == -1 || dist[i] < dist[u])) {
                u = i;
            }
        }

        // Marquer le sommet choisi comme traité
        processed[u] = true;

        // Mettre à jour la valeur de distance des sommets adjacents au sommet choisi
        for (v = 0; v < V; v++) {
            if (graph[u][v] != 0 && !processed[v]) {
                int new_dist = dist[u] + graph[u][v];
                if (new_dist < dist[v]) {
                    dist[v] = new_dist;
                }
            }
        }
    }

    // Afficher le tableau de distance construit
    afficherSolution(dist);
}


