    {
        int min_val = Integer.MAX_VALUE;
        int min_index = 0;

        // Parcourir toutes les valeurs des nœuds,
        // qui ne sont pas encore inclus dans l'ACM et trouver celui de valeur minimale.
        for(int i = 0; i < n; i++)
        {
            if (mstset[i] == false && keyvals[i] < min_val)
            {
                min_val = keyvals[i];
                min_index = i;
            }
        }
        return min_index;
    }

