            mstset[i] = false;
        }

        // Commencer à trouver l'ACM à partir du nœud 0.
        // Le parent du nœud 0 est aucun et est défini à -1.
        // La valeur clé ou le coût minimum pour atteindre le nœud 0 à partir du nœud 0 est 0.
        parent[0] = -1;
